mapper.select:
select d.service_id AS "serviceId", d.service_name AS "serviceName", a.profile_name AS "profileName", a.profile_desc AS "profileDesc", a.created_at AS "createdAt", a.updated_at AS "updatedAt", count(c.config_key) AS "configNum" from h_dbc_profiles a, h_dbc_acc_profiles b, h_dbc_config c, h_dbc_service d, h_dbc_acc_service e where a.profile_name = b.profile_name and a.profile_name = c.profile_name and b.username = c.username and d.service_id = e.service_id and b.username = e.username and c.service_id = d.service_id and c.username = #{username} and c.app = #{app} and c.service_id = #{serviceId}
and a.profile_name = #{profileName}
and a.profile_desc like #{profileDescLike}
group by d.service_id, d.service_name, a.profile_name, a.profile_desc, a.created_at, a.updated_at

mapper.update:
update h_dbc_config
set config_value=#{config.configValue},
    updated_at  = #{config.updatedAt}
where app = #{asp.app}
  and username = #{asp.username}
  and service_id = #{asp.serviceId}
  and profile_name = #{asp.profileName}
  and config_key = #{config.configKey}

mapper.delete:
delete
from h_dbc_config
where app = #{asp.app}
  and username = #{asp.username}
  and service_id = #{asp.serviceId}
  and profile_name = #{asp.profileName}
  and config_key = #{q.configKey}

mapper.select:
select config_key AS "configKey",
config_value AS "configValue",
created_at AS "createdAt",
updated_at AS "updatedAt"
from h_dbc_config
where app = #{asp.app}
and username = #{asp.username}
and service_id = #{asp.serviceId}
and profile_name = #{asp.profileName}


    and config_key like #{configKeyLike}



    and config_value like #{configValueLike}

order by config_key

mapper.update:
update h_dbc_config_file
set file_content=#{fileContent},
    updated_at=#{updatedAt}
where app = #{app}
  and username = #{username}
  and service_id = #{serviceId}
  and profile_name = #{profileName}

mapper.select:
select app          AS "app",
       username     AS "username",
       service_id   AS "serviceId",
       profile_name AS "profileName",
       file_name    AS "fileName",
       file_content AS "fileContent",
       created_at   AS "createdAt",
       updated_at   AS "updatedAt"
from h_dbc_config_file
where app = #{app}
  and username = #{username}
  and service_id = #{serviceId}
  and profile_name = #{profileName}

mapper.delete:
delete
from h_dbc_config_file
where app = #{app}
  and username = #{username}
  and service_id = #{serviceId}
  and profile_name = #{profileName}

mapper.select:
select a.username AS "username", a.service_id AS "serviceId", b.service_name AS "serviceName", a.profile_name AS "profileName", a.config_key AS "configKey", a.config_value AS "configValue", a.created_at AS "createdAt", a.updated_at AS "updatedAt" from h_dbc_config a,h_dbc_service b where a.service_id=b.service_id and app=#{app}
and username=#{username}
and config_key = #{configKey} order by a.username,b.service_name,a.profile_name
